   document.addEventListener('DOMContentLoaded', function() {
   // --- Thiết lập cảnh Three.js ---
            const container = document.getElementById('solar-system-canvas-container');
            let scene, camera, renderer, controls, sun;
            const planets = [];
            const orbitGroups = [];
            const planetInfoMap = new Map();
            const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
            let isRotating = true; // Trạng thái quay

            // Quản lý tải texture
            const loadingManager = new THREE.LoadingManager();
            loadingManager.onStart = () => console.log('Loading started');
            loadingManager.onLoad = () => console.log('All textures loaded');
            loadingManager.onError = (url) => console.error(`Error loading ${url}`);
            const textureLoader = new THREE.TextureLoader(loadingManager);

            // Kích thước tương đối (tăng kích thước để dễ nhìn)
            const sizes = {
                sun: 60, // Tăng kích thước Mặt Trời
                mercury: 7,
                venus: 10,
                earth: 11,
                mars: 8,
                jupiter: 30,
                saturn: 25,
                uranus: 18,
                neptune: 17
            };

            const distances = {
                mercury: 80,
                venus: 110,
                earth: 150,
                mars: 190,
                jupiter: 300,
                saturn: 400,
                uranus: 480,
                neptune: 550
            };

            const rotationSpeeds = {
                sun: 0.001,
                mercury: 0.01,
                venus: 0.005,
                earth: 0.02,
                mars: 0.018,
                jupiter: 0.04,
                saturn: 0.038,
                uranus: 0.012,
                neptune: 0.011
            };

            const orbitSpeeds = {
                mercury: 0.02,
                venus: 0.015,
                earth: 0.01,
                mars: 0.008,
                jupiter: 0.004,
                saturn: 0.003,
                uranus: 0.002,
                neptune: 0.0015
            };

            // Màu mặc định cho hành tinh nếu texture lỗi
            const defaultColors = {
                sun: 0xffff00, // Vàng cho Mặt Trời
                mercury: 0xaaaaaa, // Xám
                venus: 0xffcc66, // Vàng cam
                earth: 0x3366ff, // Xanh dương
                mars: 0xff3333, // Đỏ
                jupiter: 0xcc9966, // Nâu cam
                saturn: 0xffcc99, // Vàng nhạt
                uranus: 0x66cccc, // Xanh lam
                neptune: 0x3366cc // Xanh đậm
            };

            function init() {
                // Tạo Scene
                scene = new THREE.Scene();
                scene.background = textureLoader.load('https://www.shutterstock.com/shutterstock/videos/1098468553/thumb/1.jpg?ip=x480');

                // Camera
                camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
                camera.position.set(0, 100, 300);

                // Renderer
                renderer = new THREE.WebGLRenderer({ antialias: true });
                renderer.setSize(container.clientWidth, container.clientHeight);
                renderer.setPixelRatio(isMobile ? Math.min(window.devicePixelRatio, 1.5) : window.devicePixelRatio);
                renderer.gammaOutput = true;
                renderer.gammaFactor = 2.2;
                container.appendChild(renderer.domElement);

                // OrbitControls
                controls = new THREE.OrbitControls(camera, renderer.domElement);
                controls.enableDamping = true;
                controls.dampingFactor = 0.25;
                controls.enableZoom = true;
                controls.enablePan = true;

                // Ánh sáng
                const sunLight = new THREE.PointLight(0xffffff, 3.5, 0); // Tăng cường độ
                sunLight.position.set(0, 0, 0);
                scene.add(sunLight);

                const ambientLight = new THREE.AmbientLight(0xaaaaaa, 1.0); // Tăng ánh sáng môi trường
                scene.add(ambientLight);

                const directionalLight = new THREE.DirectionalLight(0xffffff, 1.2); // Tăng cường độ
                directionalLight.position.set(100, 100, 100);
                scene.add(directionalLight);

                // Tạo Mặt Trời
                const sunGeometry = new THREE.SphereGeometry(sizes.sun, 32, 32);
                const sunMaterial = new THREE.MeshBasicMaterial({
                    color: defaultColors.sun, // Màu vàng cố định
                    map: textureLoader.load('data:image/jpeg;base64,', undefined, undefined, () => {
                        console.error('Failed to load sun texture, using default color');
                    }),
                    emissive: 0xffff00, // Phát sáng vàng
                    emissiveIntensity: 0.8
                });
                sun = new THREE.Mesh(sunGeometry, sunMaterial);
                scene.add(sun);
                sun.name = 'sun';
                planetInfoMap.set(sun, 'info-sun');

                // Tạo các hành tinh
                const planetsData = [
                    { name: 'mercury', size: sizes.mercury, distance: distances.mercury, orbitSpeed: orbitSpeeds.mercury, rotationSpeed: rotationSpeeds.mercury, texture: 'data:image/jpeg;base64,', color: defaultColors.mercury },
                    { name: 'venus', size: sizes.venus, distance: distances.venus, orbitSpeed: orbitSpeeds.venus, rotationSpeed: rotationSpeeds.venus, texture: 'data:image/jpeg;base64,', color: defaultColors.venus },
                    { name: 'earth', size: sizes.earth, distance: distances.earth, orbitSpeed: orbitSpeeds.earth, rotationSpeed: rotationSpeeds.earth, texture: 'data:image/jpeg;base64,', color: defaultColors.earth },
                    { name: 'mars', size: sizes.mars, distance: distances.mars, orbitSpeed: orbitSpeeds.mars, rotationSpeed: rotationSpeeds.mars, texture: 'data:image/jpeg;base64,', color: defaultColors.mars },
                    { name: 'jupiter', size: sizes.jupiter, distance: distances.jupiter, orbitSpeed: orbitSpeeds.jupiter, rotationSpeed: rotationSpeeds.jupiter, texture: 'data:image/jpeg;base64,', color: defaultColors.jupiter },
                    { name: 'saturn', size: sizes.saturn, distance: distances.saturn, orbitSpeed: orbitSpeeds.saturn, rotationSpeed: rotationSpeeds.saturn, texture: 'data:image/jpeg;base64,', color: defaultColors.saturn, ring: true },
                    { name: 'uranus', size: sizes.uranus, distance: distances.uranus, orbitSpeed: orbitSpeeds.uranus, rotationSpeed: rotationSpeeds.uranus, texture: 'data:image/jpeg;base64,', color: defaultColors.uranus },
                    { name: 'neptune', size: sizes.neptune, distance: distances.neptune, orbitSpeed: orbitSpeeds.neptune, rotationSpeed: rotationSpeeds.neptune, texture: 'data:image/jpeg;base64,', color: defaultColors.neptune }
                ];

                planetsData.forEach(data => {
                    const segments = data.size < 10 ? 16 : 32;
                    const planetGeo = new THREE.SphereGeometry(data.size, segments, segments);
                    const planetMat = new THREE.MeshStandardMaterial({
                        map: textureLoader.load(data.texture, undefined, undefined, () => {
                            console.error(`Failed to load ${data.name} texture, using default color`);
                            planetMat.color.setHex(data.color);
                        }),
                        color: data.color, // Màu mặc định
                        roughness: 0.7,
                        metalness: 0.2
                    });
                    const planet = new THREE.Mesh(planetGeo, planetMat);

                    const orbitGroup = new THREE.Object3D();
                    scene.add(orbitGroup);
                    planet.position.set(data.distance, 0, 0);
                    orbitGroup.add(planet);

                    planet.userData = {
                        name: data.name,
                        orbitSpeed: data.orbitSpeed,
                        rotationSpeed: data.rotationSpeed,
                        orbitGroup: orbitGroup
                    };

                    planets.push(planet);
                    orbitGroups.push(orbitGroup);
                    planetInfoMap.set(planet, `info-${data.name}`);

                    if (data.ring) {
                        const ringGeometry = new THREE.RingGeometry(data.size * 1.2, data.size * 2, 32);
                        const ringMaterial = new THREE.MeshStandardMaterial({
                            map: textureLoader.load('data:image/jpeg;base64,', undefined, undefined, () => {
                                console.error('Failed to load saturn ring texture');
                                ringMaterial.color.setHex(0xcccccc);
                            }),
                            color: 0xcccccc, // Màu xám nhạt cho vành đai
                            side: THREE.DoubleSide,
                            roughness: 0.7,
                            metalness: 0.3
                        });
                        const ring = new THREE.Mesh(ringGeometry, ringMaterial);
                        ring.rotation.x = Math.PI / 2;
                        ring.rotation.y = Math.PI / 6;
                        planet.add(ring);
                    }
                });

                // Hiển thị thông tin Mặt Trời mặc định
                const defaultInfoBlock = document.getElementById('info-sun');
                if (defaultInfoBlock) {
                    defaultInfoBlock.classList.remove('d-none');
                }

                // Nút điều khiển dừng/quay
                const toggleButton = document.getElementById('toggle-rotation-btn');
                toggleButton.addEventListener('click', toggleRotation);

                // Phím cách để dừng/quay
                window.addEventListener('keydown', (event) => {
                    if (event.code === 'Space') {
                        toggleRotation();
                        event.preventDefault(); // Ngăn cuộn trang khi nhấn phím cách
                    }
                });

                // Sự kiện
                window.addEventListener('click', onClick);
                window.addEventListener('resize', onWindowResize, false);

                // Bắt đầu vòng lặp animation
                renderer.setAnimationLoop(animate);
            }

            function toggleRotation() {
                isRotating = !isRotating;
                const toggleButton = document.getElementById('toggle-rotation-btn');
                toggleButton.textContent = isRotating ? 'Dừng' : 'Quay';
            }

            function animate() {
                controls.update();

                if (isRotating) {
                    sun.rotation.y += rotationSpeeds.sun;

                    planets.forEach(planet => {
                        planet.rotation.y += planet.userData.rotationSpeed;
                        planet.userData.orbitGroup.rotation.y += planet.userData.orbitSpeed;
                    });
                }

                renderer.render(scene, camera);
            }

            const raycaster = new THREE.Raycaster();
            const mouse = new THREE.Vector2();

            function onClick(event) {
                const canvasBounds = renderer.domElement.getBoundingClientRect();
                mouse.x = ((event.clientX - canvasBounds.left) / canvasBounds.width) * 2 - 1;
                mouse.y = -((event.clientY - canvasBounds.top) / canvasBounds.height) * 2 + 1;

                raycaster.setFromCamera(mouse, camera);
                const intersects = raycaster.intersectObjects([...planets, sun], true);

                const planetDetails = document.querySelectorAll('.planet-info');
                planetDetails.forEach(detailBlock => {
                    detailBlock.classList.add('d-none');
                });

                if (intersects.length > 0) {
                    let targetObject = intersects[0].object;
                    while (targetObject && !targetObject.userData.name && targetObject !== sun) {
                        targetObject = targetObject.parent;
                    }

                    if (targetObject && (targetObject.userData.name || targetObject === sun)) {
                        const targetInfoId = planetInfoMap.get(targetObject);
                        if (targetInfoId) {
                            const targetInfoBlock = document.getElementById(targetInfoId);
                            if (targetInfoBlock) {
                                targetInfoBlock.classList.remove('d-none');
                                const detailsSection = document.getElementById('planet-details');
                                if (detailsSection) {
                                    if ('scrollBehavior' in document.documentElement.style) {
                                        detailsSection.scrollIntoView({ behavior: 'smooth', block: 'start' });
                                    } else {
                                        detailsSection.scrollIntoView();
                                    }
                                }
                            }
                        }
                    }
                }
            }

            function onWindowResize() {
                const newWidth = container.clientWidth;
                const newHeight = container.clientHeight;
                camera.aspect = newWidth / newHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(newWidth, newHeight);
            }

            init();
}); // End DOMContentLoaded